mapper.select:
select company_code
from bid
where tender_code=#{tender_code}

mapper.delete:
delete
from bid
where tender_code=#{tender_code} and company_code=#{company_code}

mapper.select:
select upload_code
from bid
where tender_code=#{tender_code} and company_code=#{company_code}

mapper.update:
update bid
set bid_ppt_score = ${bid_ppt_score}
where company_code = #{company_code} and tender_code=#{tender_code}
