mapper.select:
select x.* from(select rownum as rnum,a.* from(select rownum,t.* from (select * from tender
where member_id=#{member_id}
order by t_creation_date asc) t order by rownum desc) a where rownum <= #{endnum} ) x where x.rnum>=#{startnum}

select count(*)
from bid
where tender_code=#{tender_code}

select tender_deadline-trunc(sysdate)
from tender
where tender_code=#{tender_code}

select tender_code,tender_title, t_creation_date,to_char(t_creation_date,'yyyy-MM-dd HH:mm') as tcreationdate, to_char(service_date,'yyyy-MM-dd') as service_date,  to_char(tender_deadline,'yyyy-MM-dd') as tender_deadline, calculate_period,t_space, floor_number,requirement, t_zipcode, t_road_addr, t_addr, t_addr_detail, member_id, tender_name
from tender 
where tender_code=#{tender_code}

select member_id
from tender
where tender_code=#{tender_code}

select calculate_period
from tender
where tender_code=#{tender_code}

		select count(*)
   		from tender
