mapper.select:
select measure_value value, to_char(measure_time,'YYYY-MM-DD HH24:MI:SS') time
from(
		select measure_value, measure_time
		from measure_data
		where iot_id = #{iot_id}
		and matter_code = #{matter_code}
		order by measure_time desc)
where rownum <=30
order by to_char(measure_time,'YYYY-MM-DD HH24:MI:SS')

select nvl(to_char(avg(measure_value),'9999999999.9'),0) today_avg
from measure_data
where iot_id = #{iot_id}
and matter_code = #{matter_code}
and to_char(measure_time,'yyyy/mm/dd') = to_char(sysdate,'yyyy/mm/dd')

select count(*)
from measure_data
where iot_id = #{iot_id}
and matter_code = #{matter_code}
and to_char(measure_time,'yyyy/mm/dd') = to_char(sysdate,'yyyy/mm/dd')
and measure_value > 50


select to_char(measure_time,'mm') month, to_number(to_char(avg(measure_value),'9999999999.9')) value
from measure_data
where iot_id = #{iot_id}
and matter_code = #{matter_code}
group by to_char(measure_time,'mm')
order by to_char(measure_time,'mm')


		select decode(to_char(to_date(r.day,'yyyy-mm-dd'),'d'),1,'일',2,'월',3,'화',4,'수',5,'목',6,'금',7,'토') day, r.value
		from(      
        		select to_char(measure_time,'yyyy-mm-dd') day, to_number(to_char(avg(measure_value),'9999999999.9')) value
        		from measure_data
        		where iot_id = #{iot_id}
        		and matter_code = #{matter_code}
        		and to_char(measure_time,'yyyy-mm-dd') > to_char(sysdate-7,'yyyy-mm-dd')
        		group by to_char(measure_time,'yyyy-mm-dd')
        		order by to_char(measure_time,'yyyy-mm-dd')) r

select measure_value value, to_char(measure_time,'YYYY-MM-DD HH24:MI:SS') time
from(
		select measure_value, measure_time
		from measure_data
		where iot_id = #{iot_id}
		and matter_code = #{matter_code}
		order by measure_time desc)
where rownum <=1
